mapper.select:
SELECT o.*, p.*, i.store_img FROM Orders o JOIN Product p ON o.product_no = p.product_no LEFT JOIN ( SELECT store_id, store_img FROM Image WHERE img_no IN ( SELECT MIN(img_no) FROM Image GROUP BY store_id ) )i ON o.store_id = i.store_id
o.store_id=#{storeId}
AND o.order_status IN ('confirmed', 'pickup')
AND o.order_status=#{type}
AND MONTH(o.ordered_at) = MONTH(CURDATE()) AND YEAR(o.ordered_at) = YEAR(CURDATE())
ORDER BY o.ordered_at DESC

mapper.update:
UPDATE Orders
SET order_status=#{type}
WHERE order_no=#{id}

UPDATE Orders 
SET order_code=#{randomCode}
WHERE order_no=#{id} AND order_status=#{type}

UPDATE Orders
SET order_status=#{type}, canceld_from="store"
WHERE order_no=#{id}

mapper.insert:
INSERT INTO Alarm(
	alarm_title, alarm_contents, sended_at, alarm_displayed, receive_id, alarm_read
) VALUES (
	#{alarm_title}, #{alarm_contents}, #{sended_at}, #{alarm_displayed}, #{receive_id}, #{alarm_read}
)

mapper.update:
UPDATE Orders
SET order_status=#{type}, picked_at=NOW()
WHERE order_no=#{id}

UPDATE Store
SET store_status = #{status}
WHERE store_id = #{store_id}
